    /**
     * 当日发稿总数
     * @param
     * @return
     */
    @RequestMapping(value = "channel/counts", method = GET)
    public String getCounts() {
        def data = [:]
        Random random = new Random()
        int increase = random.nextInt(5)
        count = count + increase
        if (count >= 1500) {
            count = 603
        }
        data.put("name", "")
        data.put("value", count)
        String result = JSONObject.toJSON(data)
        result = "[" + result + "]"
        return result
    }

    /**
     * 站点发稿趋势
     * @return
     */
    @RequestMapping(value = "channel/monthData", method = GET)
    public String getMonthData() {
        ArrayList dataList = new ArrayList()
        SimpleDateFormat sdf = new SimpleDateFormat("MM-dd")
        Date nowDate = new Date(1531843200000)
        Random random = new Random()
        for (int i = 4; i >= 0; i--) {
            Calendar calendar = Calendar.getInstance()
            calendar.setTime(nowDate)
            calendar.add(Calendar.DAY_OF_MONTH, -i)
            String time = sdf.format(calendar.getTime())
            for (int t = 0; t < 3; t++) {
                double rate = random.nextDouble() * 0.25 + 0.75
                def data = [:]
                data.put("x", time)
                data.put("s", (t + 1).toString())
                if(t==0){
                    data.put("y", (1502 * rate).intValue())
                }else if (t == 1){
                    data.put("y", (201 * rate).intValue())
                }else if (t == 2){
                    data.put("y", (432 * rate).intValue())
                }
                dataList.add(data)
            }
        }
        String result = JSONObject.toJSON(dataList)
        return result
    }

